
public class PizzaBill 
{

	public static void main(String[] args) 
	{
		// TODO Auto-generated method stub
     int cp=399;
     float bp=0.8f*2*cp;
     System.out.println("Domino's Pizza is giving an offer of 20% discount on purchasing 2 pizzas");
     System.out.println("So Mr. Sampath ordered 2 pizzas, each costing Rs."+cp+"/-");
     System.out.println("The final Bill Price Mr Sampath has to pay is Rs."+bp+"/-");
	}

}
