
public class Taxamount 
{

	public static void main(String[] args) 
	{
	 	// TODO Auto-generated method stub
      int sal=85000;
      int taxper=20;
      float taxam=(taxper*sal)/100;
      System.out.println("Vikram's Salary is Rs."+sal+"/-");
      System.out.println("Tax percentage he has to pay is "+taxper+"%");
      System.out.println("Hence the Tax Amount he pays is Rs."+taxam+"/-");
	}

}
